package aula01;

public class Elefante extends Animal {
	
	private double tamanhoPresa;
	private boolean motivacao;
	
	public Elefante() {
		// TODO Auto-generated constructor stub
	}

	public double getTamanhoPresa() {
		return tamanhoPresa;
	}

	public void setTamanhoPresa(double tamanhoPresa) {
		this.tamanhoPresa = tamanhoPresa;
	}

	public boolean isMotivacao() {
		return motivacao;
	}

	public void setMotivacao(boolean motivacao) {
		this.motivacao = motivacao;
	}
}
